import { Component } from '@angular/core';
import { Router } from '@angular/router';

import { UserService } from '../services/user.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  username: string = "";
  password: string = "";

  constructor(private userService: UserService, private router: Router){}

  authenticate(){
    const success = this.userService.authenticate(this.username, this.password);
    if(!success){
      this.router.navigate(['invalid-credentials']);
    }else this.router.navigate(['profile']);
  }
}
<body>
  <div class="container loginBody">
    <img src="../../assets/alms-profile.png" class="img">
    <form>
      <div class="form-group">
        <input type="text" class="form-control" placeholder="Enter Username" name="username" [(ngModel)]="username">
      </div>
      <div class="form-group">
          <input type="password" class="form-control" placeholder="Enter Password" name="password" [(ngModel)]="password">
      </div>
      <button type="submit" class="btn btn-primary" (click)="authenticate()">Login</button>
    </form>  
  </div>
</body>
