import { Injectable } from '@angular/core';
import {User} from "./User";

@Injectable({
  providedIn: 'root'
})
export class UserService {

  user_db : User[] = [];

  constructor() { }

  addUser(username:string, password: string, age: number, email: string): void{
    const user : User = {
      "username": username,
      "age": age,
      "email": email,
      "password": password 
    }

    this.user_db.push(user);
  }

  getRegisteredUsers(): User[]{
    return this.user_db;
  }

  authenticate(username:string, password:string): boolean{
    for(let i=0; i<this.user_db.length; i++){
      if(this.user_db[i].username==username && this.user_db[i].password==password){
        console.log("Valid Credentials for user "+username);
        return true;
      }
    }
    console.log("Invalid Credentials.");
    return false;
  } 
}
