import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { ConstantsService } from '../services/constants.service';


@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  username: string = "";
  password: string = "";
  loginStatus: string = "";
  role: string = "";


  constructor(private constantsService: ConstantsService) { }

  authenticate(){
    let url = "";
    if(this.role=="admin") url = this.constantsService.SERVER_IP_ADDRESS + "admin";
    else if(this.role=="productionManager") url = this.constantsService.SERVER_IP_ADDRESS + "productionManager";
    else if(this.role=="lineChief") url = this.constantsService.SERVER_IP_ADDRESS + "lineChief";
    else if(this.role=="supervisor") url = this.constantsService.SERVER_IP_ADDRESS + "supervisor";

    const data = {
      "operation":"login",
      "username": this.username,
      "password": this.password
    };

    fetch(url, {
      method: "POST",
      mode: "cors", 
      cache: "no-cache",
      credentials: "same-origin",
      headers: {"Content-Type": "application/json",},
      redirect: "follow",
      referrerPolicy: "no-referrer",
      body: JSON.stringify(data),
  })
      .then((resolve)=>{
          console.log("Authentication has been done!");
          return resolve.json()
      })
      .then((data)=>{
          console.log(data.authentication);
          this.loginStatus = "Status: "+data.authentication;
          sessionStorage.setItem("userid", data.userInfo.userid);
          sessionStorage.setItem("username", data.userInfo.username);
          sessionStorage.setItem("password", data.userInfo.password);
          sessionStorage.setItem("age", data.userInfo.age);
          sessionStorage.setItem("role", data.userInfo.role);
          sessionStorage.setItem("general_info", data.userInfo.general_info);
          sessionStorage.setItem("userHash", data.userHash);

        if(data.nextPage!="none") window.location.href = data.nextPage;
      })
      .catch((err)=>{
          console.log(err);
      });
  }

}

  <div class="container loginBody">
    <img src="../../assets/alms-profile.png" class="img">
    <form>
      <div class="form-group">
        <input type="text" class="form-control" placeholder="Enter Username" name="username" [(ngModel)]="username">
      </div>
      <div class="form-group">
          <input type="password" class="form-control" placeholder="Enter Password" name="password" [(ngModel)]="password">
      </div>

      <div class="form-group dropDown">
        <select class="form-control" [(ngModel)]="role" name="role">
          <option value="admin" [defaultSelected]="asd">Select Role--</option>
          <option value="admin">Admin</option>
          <option value="productionManager">Production Manager</option>
          <option value="lineChief">Line Chief</option>
          <option value="supervisor">Supervisor</option>
        </select>
      </div>     

      <button type="submit" class="btn btn-primary" (click)="authenticate()">Login</button>

      <span class="badge badge-secondary">{{loginStatus}}</span>
    </form>  
  </div>
