import { Component } from '@angular/core';
import { User } from 'src/app/models/User';

@Component({
  selector: 'app-register-user',
  templateUrl: './register-user.component.html',
  styleUrls: ['./register-user.component.css']
})
export class RegisterUserComponent {
  user : User = new User();
  password: string = "";
  status : string = "";

  registerUser():void{
    const username = document.getElementById("userNameId").value;
    const password = document.getElementById("password").value;
    const age = document.getElementById("age").value;
    const generalInfo = document.getElementById("generalInfoId").value;
    const role = document.getElementById("role").value;


    let url = ip_addr + "admin";
    let data = {
        "operation":"rnu",
        "username": username,
        "password": password,
        "age": age,
        "generalInfo": generalInfo,
        "role": role,
        "userHash": userHash
    }

    console.log(data);

    fetch(url, {
        method: "POST",
        mode: "cors", 
        cache: "no-cache", 
        credentials: "same-origin", 
        headers: {"Content-Type": "application/json",},
        redirect: "follow", 
        referrerPolicy: "no-referrer", 
        body: JSON.stringify(data),
    })
    .then((resolve)=>{
        console.log("Registration Request has been resolved!");
        return resolve.json()
    })
    .then((data)=>{
        this.status = "User Id for registered user: "+data.RegisteredUserId;
    })
    .catch((err)=>{
    console.log(err);
    });

  }
}
<div class="row" id="userRegister">
    <div class="p-5 text-center bg-light">
      <h4 class="mb-3">Register New User</h4>
    </div>

      <div class="form-group">
        <input type="text" class="form-control" placeholder="Enter User Name" name="username" [(ngModel)]="user.username">
      </div>
      <div class="form-group">
        <input type="password" class="form-control" placeholder="Set password" name="password" [(ngModel)]="password">
      </div>
      <div class="form-group">
        <span>Select Role:</span>
        <select class="form-control" name="role" [(ngModel)]="user.role">
          <option value="admin">Admin</option>
          <option value="productionManager">Production Manager</option>
          <option value="lineChief">Line Chief</option>
          <option value="supervisor">Supervisor</option>
        </select>
      </div> 
      <div class="form-group">
        <input type="number" class="form-control" placeholder="Enter Age" name="age" [(ngModel)]="user.age">
      </div>
      <div class="form-group">
        <input type="text" class="form-control" placeholder="Other Info" name="generalInfo" [(ngModel)]="user.general_info">
      </div>
      <button type="submit" class="btn btn-default" (click)="registerUser()">Register</button>
      <span id="status">{{status}}</span>
  </div>
